AddUnitText = {} function AddUnitText.prerequisitesPresent(specializations) return true end function AddUnitText.registerEventListeners(vehicleType) SpecializationUtil.registerEventListener(vehicleType, "onLoad", AddUnitText) end function AddUnitText:onLoad(savegame) if Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("netWrap")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("claas_netWrap")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("kuhn_netWrap")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("kverneland_netWrap")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("poettinger_netWrap")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("vicon_netWrap")], false) then local twineFillLevel = #self.spec_fillUnit.fillUnits self.spec_fillUnit.fillUnits[twineFillLevel].unitText = g_i18n:getText("unit_meter", self.customEnvironment) end end