AddUnitText = {} function AddUnitText.prerequisitesPresent(specializations) return true end function AddUnitText.registerEventListeners(vehicleType) SpecializationUtil.registerEventListener(vehicleType, "onLoad", AddUnitText) end function AddUnitText:onLoad(savegame) if Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("twine")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("caseIH_twine")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("claas_twine")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("kuhn_twine")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("krone_twine")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("masseyFerguson_twine")], false) or Utils.getNoNil(self.spec_fillUnit.fillUnits[#self.spec_fillUnit.fillUnits].supportedFillTypes[g_fillTypeManager:getFillTypeIndexByName("newHolland_twine")], false) then local twineFillLevel = #self.spec_fillUnit.fillUnits self.spec_fillUnit.fillUnits[twineFillLevel].unitText = g_i18n:getText("unit_meter", self.customEnvironment) end end